INSERT INTO public.medidas (id_medida,id_organismo,referencia_pda,tipo_medida,nombre_corto,indicador,formula,frecuencia,verificacion,regulatoria) VALUES
	 (1,12,'42, 43, 44','','RCA que contenga obligación de compensar emisiones.','Número de RCA aprobadas en el año t que contengan obligaciones de compensar emisiones atmosféricas.','Suma del número de RCA aprobadas que contengan obligación de compensar emisiones atmosféricas.','Anual.','Registro de las RCA aprobadas identificando el titular, la RCA, las emisiones y el monto a compensar.','Regulatoria'),
	 (2,13,'33.37','','Condiciones del sistema de recuperación y/o eliminación de vapores de estanques Depósito techo fijo.','Cumplimiento de las condiciones indicadas en el literal A) para depósitos de techo fijo y Cronograma de implementación gradual calificado por la SEC cuando corresponda','([N° de tanques del artículo 33 literal A) al cual se han implementado las medidas comprometidas en el año t]/ [N° de tanques del artículo 33 literal A) programadas para el año t]) *100','Anual','a) Informe de Avance de Implementación de las medidas del Plan. b) En caso de solicitar más plazo, Oficio de envío de la Resolución que califica el Cronograma de implementación gradual para el plazo de cumplimiento.','Regulatoria'),
	 (3,13,'33.37','','Requisitos del sistema de almacenamiento intermedio','Instrucciones de SEC, para cumplir con el sistema de almacenamiento intermedio u otro con el mismo objetivo, conforme al artículo 5 del DS N°160/2008','Si/No','Única','Oficialización de la instrucción de SEC para cumplir con el sistema indicado en el artículo 33 del Plan.','Regulatoria'),
	 (4,13,'33.37','','Aprobación de sistema de almacenamiento intermedio','N° de instalaciones con estanque que cuenten con un sistema de almacenamiento intermedio u otro que cumpla con el objetivo aprobado.','Si/No','Única','Resolución que aprueba por la SEC, el sistema intermedio u otro que cumpla con el mismo objetivo','Regulatoria'),
	 (5,13,'33.37','','Condiciones del sistema de recuperación y/o eliminación de vapores de estanques Deposito de techo flotante','Cumplimiento de las condiciones indicadas en el literal B) para depósitos de techo flotante y Cronograma de implementación gradual calificado por la SEC, cuando corresponda.','([N° de tanques a los cuales se les implementaron sellos primarios y/o secundarios l en el año t] / [N° de tanques programados implementados en el año t]) * 100.','Anual','a) Informe de avance de implementación de las medidas del articulo 33 del plan. b) En caso de solicitar más plazo, Oficio de envío de la Resolución que califica el cronograma de implementación gradual para el plazo de cumplimiento.','Regulatoria'),
	 (6,13,'34','','Sistema de recuperación y eliminación de vapores en fuentes emisoras de HC','N° de sistemas capaz de recuperar y/o eliminar vapores que se generen en los procesos de carga y descarga, transporte, almacenamiento, distribución y abastecimiento de HC y sus derivados, en el año t.','([N° de sistemas de recuperación y/o eliminación de vapores mantenidos con TK mayores a 200m3] / [N° de sistemas de recuperación y/o eliminación de vapores existentes en instalaciones con TK mayores a 200m3]) *100','Anual','Programa de mantención y operación de los dispositivos y/o infraestructura, recepcionado hasta mayo de cada año a la SEC.','Regulatoria'),
	 (7,13,'34','','Almacenamiento, distribución de combustibles líquidos','Número de fiscalizaciones a las obligaciones del art. 177 letra g) del DS N°160/2008 Ministerio de Economía y Fomento y Reconstrucción, en el año t.','Suma del número de fiscalizaciones ejecutadas en el año t a instalaciones de almacenamiento y distribución de combustible','Anual','a) Registro interno del servicio con el número de fiscalizaciones ejecutadas a instalaciones de almacenamiento y distribución de combustibles líquidos. b) Programa de mantención y operación de los dispositivos y/o infraestructura, recepcionado hasta mayo de cada año a la SEC.','Regulatoria'),
	 (8,13,'35','','Sistemas de venteo dotados de piloto manual y automático','N° de Instalaciones con antorcha que cuenten con piloto de encendido manual y automático en el sistema de venteo (antorcha).','Si/No','Única','Registro fotográfico del piloto de encendido manual y automático, enviado a la SEC. Registro del sistema de control de los sistemas de encendido, enviado a la SEC.','Regulatoria'),
	 (9,13,'35','','Registro trazable de los flujos másicos del gas piloto y de gas barrido','Numero de fiscalizaciones a establecimientos que cuenten con sistemas de venteo en los que realice quema controlada que den cuenta del registro trazable de los flujos masicos horarios del gas piloto y de gas de barrido.','Suma del número de fiscalizaciones a establecimiento que cuenten con sistema de venteo con quema controlada mediante antorcha','Anual','Registro interno del servicio del número de fiscalizaciones donde se verifico el cumplimiento del registro trazable actualizado, en formato físico y digital.','Regulatoria'),
	 (10,8,'46.d','Política Pública.','Plan Comunicacional respecto de la gestión de episodios críticos.','Implementación de Plan Comunicacional en periodo de gestión de episodios críticos.','SI / No.','Anual','Informe que dé cuenta de la implementación anual del plan comunicacional orientado a la comunidad.','No Regulatoria');
INSERT INTO public.medidas (id_medida,id_organismo,referencia_pda,tipo_medida,nombre_corto,indicador,formula,frecuencia,verificacion,regulatoria) VALUES
	 (11,8,'47, 48','Política Pública.','Declaración de episodios críticos.','Porcentaje de Resoluciones dictadas en relación al número de episodios declarados a la comunidad en el año t.','[Número de resoluciones dictadas] / [Numero de episodios declarados] *100','Anual','Resoluciones que declaran episodios críticos para el año t.','No Regulatoria'),
	 (12,8,'45','Política Pública.','Coordinación de Episodios Críticos.','Programa de coordinación de gestión de Episodios Críticos.','Si / No','Anual','a) Oficialización del programa de coordinación de episodios críticos del año t. b) Informe de resultados del programa de coordinación de episodios críticos del año t.','No Regulatoria'),
	 (13,7,'38','','Medidas para buques que realizan carga y descarga en la bahía-MARPOL','Numero de inspecciones realizadas en el año t al cumplimiento a las exigencias del anexo VI del convenio MARPOL y sus enmiendas, en naves que realicen faenas de carga y descarga en muelles, monoboyas, boyas multipropósito asociados a los terminales marítimos de la bahía de Quinteros','Suma del número de inspecciones realizadas en el año t a naves que realicen faenas de carga y descarga en los terminales marítimos de la bahía de Quinteros','Anual','a) Informe anual que dé cuenta del cumplimiento a las exigencias, sanciones impuestas y nivel de actividad de naves e la Bahía. B) Registro interno de las inspecciones realizadas en el año t en marco del anexo VI del convenio MARPOL','Regulatoria'),
	 (14,6,'39','','Prohibición de uso de fuego para la combustión abierta de rastrojos y de cualquier residuo de origen vegetal agrícola o forestal en el área rural.','Porcentaje de patrullas de detección de quemas no autorizadas ejecutados en el año t en el área del PPDA, respecto del número de patrullajes para detección de quemas no autorizadas programadas en el año t en el área de PPDA.','N° de patrullajes para detección e quemas no autorizadas, ejecutados en el año t en el área del PPDA / N° total de patrullajes patrullajes para detección de quemas no autorizadas, programadas en el año t en el área de PPDA','Anual','Reporte y patrullajes programados para detección de quemas no autorizadas de desechos vegetales agrícolas o forestales efectivamente ejecutados.','Regulatoria'),
	 (15,6,'39','','Autorización excepcional para la quema de rastrojos y de cualquier otro residuos de origen vegetal agrícola o forestal en el área rural','Numero de autorizaciones excepcionales para la ejecución de quemas agrícolas realizadas en el año t','Suma del número de autorizaciones excepcionales para la ejecución de quemas agrícolas realizadas en el año t','Anual','Registro de autorizaciones excepcionales a la ejecución de quemas de rastrojos y cualquier otro residuo de origen vegetal agrícola o forestal en el área rural.','Regulatoria'),
	 (16,11,'39','','Prohibición de uso de fuego para la combustión abierta de rastrojos, hojas secas y de cualquier residuo de origen vegetal o forestal-SAG','Numero de actividades de vigilancia de quemas agrícolas no autorizadas, en territorio rural del PPDA','Suma del número de actividades de vigilancia de quemas agrícolas, en el territorio rural del PPDA, en año t','Anual','Acta de fiscalización transversal (documento interno de registro de actividades de fiscalización)','Regulatoria'),
	 (17,5,'39','','Prohibición de uso de fuego para la combustión abierta de rastrojos, hojas secas y cualquier residuo de origen vegetal o forestal','N° de quemas de rastrojos y de cualquier tipo de vegetación viva o muerta, detectada en los terrenos agrícolas ganaderos o de aptitud preferente forestal detectadas en el año t','Suma del número de quemas en terrenos agrícolas ganaderos o de aptitud preferentemente forestal en el año t','Anual','Registro interno del servicio de cuentas de las quemas agrícolas detectadas en período definido según cronograma quemas de rastrojos','Regulatoria'),
	 (18,10,'27','Política pública','Catastro de plantas de áridos e instalaciones que utilizan áridos','Existencia de Catastro y Diagnóstico de las Plantas de áridos de instalaciones que utilizan áridos para mejoramiento o mantención de superficies metálicas','Sí/No','Única','Oficialización del Catastro de Diagnóstico de las Plantas de áridos e instalaciones que utilizan áridos para mejoramiento o mantención de superficies metálicas','No Regulatoria'),
	 (19,14,'27','','Requisitos para fuentes areales existentes que cuenten con proceso de extracción, molienda harneo de áridos y su uso para mejoramiento de superficies metálicas','Cumplimiento de las medidas listadas en las letras a) a f) del artículo 27 del Plan, para las fuentes areales existentes, en el año t','([N° de titulares de procesos definidos en el art. 27 que implementaron las medidas del artículo 27 literal a) a la f) en el año t]/[N° de titulares de procesos definidos en el artículo 27 que deben cumplir las medidas del literal a) a la f) en el año t])*100','Anual','a) Informe Anual del cumplimiento de las medidas  b) Registro interno de las fiscalizaciones realizadas','Regulatoria'),
	 (20,14,'27','','Requisitos para fuentes areales nuevas que cuenten con proceso de extracción, molienda harneo de áridos y su uso para mejoramiento de superficies metálicas','Cumplimiento de las medidas listadas en las letras a) a f) del artículo 27 del Plan, para las fuentes areales existentes, en el año t','([N° de titulares de procesos definidos en el art. 27 que implementaron las medidas del artículo 27 literal a) a la f) en el año t]/[N° de titulares de procesos definidos en el artículo 27 que deben cumplir las medidas del literal a) a la f) en el año t])*100','Anual','a) Informe Anual de cumplimiento de las medidas. B) Registro interno de las fiscalizaciones realizadas','Regulatoria');
INSERT INTO public.medidas (id_medida,id_organismo,referencia_pda,tipo_medida,nombre_corto,indicador,formula,frecuencia,verificacion,regulatoria) VALUES
	 (21,14,'27','','Programa de fiscalización de áridos','Resultados del Programa de fiscalización diseñado en el año t','Si/No','Anual','a) Oficio de envío a la SMA con el programa de fiscalización a implementar en el año t b) Documento programado de fiscalización a implementar en el año t','Regulatoria'),
	 (22,14,'28','','Control de emisiones MP gráneles sólidos existentes','Cumplimiento de las medidas listadas en las letras a) a g) del artículo 28 del Plan, para las fuentes areales existentes, en el año t','([N° de titulares de instalaciones existentes definidas en el artículo 28 que implementaron las medidas del artículo 28 literal a) a la g) en el año t]/[N° de titulares de instalaciones existentes definidos en el artículo 28 que deben cumplir las medidas del artículo 28 literal a) a la g) fiscalizados en el año t])*100','Anual','Informe Anual del cumplimiento de las medidas','Regulatoria'),
	 (23,14,'28','','Control de emisiones MP gráneles sólidos nuevos','Cumplimiento de las medidas listadas en las letras a) a g) del artículo 28 del Plan, para las fuentes areales nuevas, en el año t','([N° de titulares de instalaciones nuevas definidas en el artículo 28 que implementaron las medidas del artículo 28 literal a) a la g) en el año t]/[N° de titulares de instalaciones nuevas definidos en el artículo 28 que deben cumplir las medidas del artículo 28 literal a) a la g) fiscalizados en el año t])*10','Anual','Informe Anual del cumplimiento de las medidas','Regulatoria'),
	 (24,14,'29','','Control de emisiones de MP en almacenamiento existentes de gráneles sólidos en canchas abiertas','N° de instalaciones existentes que cuenten con un sistema que impida la dispersión del material hacia el exterior, y que cuenten con un mecanismo de verificación de su efectividad, con informe favorable de la SEREMI MA, presentado a la SEREMI de Salud.','Sí/No','Única','a) Informe anual del cumplimiento de las medidas b) Resolución de SEREMI de Salud que aprueba el sistema y el mecanismo de verificación c) Informe favorable de la SEREMI de MA, del sistema y el mecanismo de verificación','Regulatoria'),
	 (25,14,'29','','Control de emisiones de MP en almacenamiento nuevos de gráneles sólidos en canchas abiertas','N° de instalaciones nuevas que cuenten con un sistema que impida la dispersión del material hacia el exterior, y que cuenten con un mecanismo de verificación de su efectividad, con informe favorable de la SEREMI MA, presentado a la SEREMI de Salud.','Sí/No','Única','a) Informe anual del cumplimiento de las medidas b) Resolución de SEREMI de Salud que aprueba el sistema y el mecanismo de verificación c) Informe favorable de la SEREMI de MA, del sistema y el mecanismo de verificación','Regulatoria'),
	 (26,14,'30','','Fuentes Areales (instalaciones existentes que no puedan contar con un sistema de dispersión del art. 29)','N° de pronunciamientos a los informes técnico presentado a la SEREMI de Salud a 2 meses de su ingreso.','Suma del número de pronunciamientos (aprobación/rechazo) de los informes técnicos presentado a la SEREMI de Salud a 2 meses de su ingreso','Única','Resolución que aprueba/rechaza el Informe Técnico que acredita el impedimento de contar con un sistema que impide la dispersión de sus graneles sólidos.','Regulatoria'),
	 (27,14,'32','','Declaración Anual de las emisiones de COVS','Declaración Anual de las emisiones de COVs del año anterior mediante el D.S. N°138/2005 MINSAL, considerando los contenidos del artículo 32 delo Plan, en el año t','Si/No','Anual','a) Registro interno de los establecimientos que presentaron declaración de emisiones de COVs en la zona saturada b) Comprobante de declaraciones de emisiones de COVs en zona de Concón, Quintero y Puchuncaví recepcionadas en la SEREMI de Salud.','Regulatoria'),
	 (28,14,'9','Política Púbica','Registro de calderas histórico','Remisión del listado histórico de calderas registradas de acuerdo al D.S N°10/2012 MINSAL a la SMA','Si/No','Única','a) Oficio de remisión de listado de calderas registradas ingresando a la SMA b) Documento Excel con listado histórico de calderas registradas.','No Regulatoria'),
	 (29,14,'9','Política Púbica','Registro actualizado de calderas','Remisión a la SMA del listado de calderas de acuerdo al D.S N° 10/2012 MINSAL en el año t','Si/No','Anual','a) Oficio de remisión de listado de calderas registradas ingresando a la SMA b) Documento Excel con listado  de calderas registradas en el año t','No Regulatoria'),
	 (30,14,'20','Política Pública','Remisión a la SMA del registro de las fuentes estacionarias','Remisión de información histórica de emisiones, por establecimiento y contaminantes','Si/No','Única','a) Oficio de derivación a la SMA de información histórica de emisiones por establecimientos y contaminante b) Excel con información histórica, por establecimiento y contaminante','No Regulatoria');
